<script>
    export default {
        name: 'HabilidadesS',
        data: ()=>({
            activeSVG: null,
            visivel: 0
        }),
        methods:{
            //seleciona o SVG clicado e adiciona as classes de elemento ativo
            activated(event){
                if(this.activeSVG !== null){
                    this.activeSVG.path[1].classList.remove('activ');
                    this.activeSVG.path[1].parentNode.children[1].classList.remove('AcBorder');
                }
                 event.path[1].classList.add('activ');
                 event.path[1].parentNode.children[1].classList.add('AcBorder')
                 this.activeSVG = event;
            }
        }
    }
